import React from "react";

import { fireEvent, render, screen } from "@testing-library/react";

import { SVGMap } from "./SVGMap";

test("correctly renders map label and viewBox", () => {
  const map = {
    id: 0,
    className: "WorldCountries",
    key: "world-countries",
    viewBox: "0 0 0 0",
    label: "Map of World Countries",
  };

  render(<SVGMap map={map} />);

  const svgElement = screen.getByLabelText(map.label);
  expect(svgElement).toHaveAttribute("viewBox", map.viewBox);
});

test("correctly applies svg styling", () => {
  const map = {
    id: 0,
    className: "WorldCountries",
    key: "world-countries",
    viewBox: "0 0 0 0",
    label: "Map of World Countries",
  };

  const mapStyle = {
    fill: "red",
    margin: "20px",
  };

  render(<SVGMap map={map} mapStyle={mapStyle} />);

  const svgElement = screen.getByLabelText(map.label);
  expect(svgElement).toHaveStyle("fill: red; margin: 20px;");
});

